mapper.select:
SELECT a.id, a.question_id, a.student_id, a.answer_text, a.create_time FROM online_answer a INNER JOIN online_question q ON a.question_id = q.id WHERE q.course_id IN
#{cid}
ORDER BY a.create_time ASC

SELECT id, question_id, student_id, answer_text, create_time
FROM online_answer
WHERE id = #{id}

mapper.delete:
DELETE FROM online_answer
WHERE question_id IN

    #{qid}
